import sys

def how_many_are_odd(a):
    count = 0
    for number in a: 
        if number % 2 != 0:
            count = count + 1
    return count

def sum_of_all_even_numbers(a):
    total = 0
    for number in a:
        if number % 2 == 0:
            total = total + number
    return total

def how_many_words_of_length_5(a):
    count = 0
    for word in a:
        if len(word) == 5:
            count = count + 1
    return count

